    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
          JFileChooser Chooser = new JFileChooser();
        Chooser.setMultiSelectionEnabled(true);
        Chooser.setFileSelectionMode(JFileChooser.FILES_ONLY);
        int result = Chooser.showDialog(this,"Open");
        if(result == JFileChooser.APPROVE_OPTION)
        {
            File file = Chooser.getSelectedFile();
            jTextField3.setText(file.toString());
       }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        try {
               String f = jTextField1.getText();
               Class.forName("com.mysql.jdbc.Driver");
               Connection conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/crime", "root", "root");
               PreparedStatement st = conn.prepareStatement("delete from new_details where cr_id = '"+f+"'");
              // ResultSet rs = 
               int i = st.executeUpdate();
               if(i!=0){
               JOptionPane.showMessageDialog(null,"ID " +f+ " Details Deteleted");
               jTextField1.setText("");
               }
               }catch (ClassNotFoundException ex) {
            System.out.print(ex);
          }
         catch (SQLException ex1) {
              System.out.print(ex1);
            }
    }//GEN-LAST:event_jButton1ActionPerformed

