    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
         try {   
                 String id = jTextField2.getText();
                 String fileName = jTextField3.getText();
                 File file = new File(fileName);
                 FileInputStream fis = new FileInputStream(file);
                 
                      Class.forName("com.mysql.jdbc.Driver");
                      Connection conn = DriverManager.getConnection("jdbc:mysql://localhost:3306/crime", "root", "root");
                      PreparedStatement pstmt = conn.prepareStatement("UPDATE new_details SET image = (?) WHERE cr_id = ? ");
                      pstmt.setBinaryStream(1, (InputStream)fis, (int)(file.length()));              
                      pstmt.setString(2, id);
                      int i = pstmt.executeUpdate();                     
                     if(i!=0){
                         JOptionPane.showMessageDialog(null,"Criminal Image Replaced..!","success",1);
                         jTextField2.setText("");
                         jTextField3.setText("");
                     }                         
              }  catch (ClassNotFoundException ex) 
                   {
                   System.out.println(ex);
                   }
                 catch (SQLException ex1) 
                  {
                    System.out.println(ex1);
                  }
                  catch(IOException e){
                  System.out.println(e);
                  } 
         
    }//GEN-LAST:event_jButton2ActionPerformed

